import React, { useState } from 'react';
import {
  Avatar,
  Box,
  Button,
  IconButton,
  Popover,
  Typography,
  List,
  ListItem,
  ListItemButton,
  ListItemText,
  Divider,
  Switch
} from '@mui/material';
import MoreVertIcon from '@mui/icons-material/MoreVert';

const UserMenu = () => {
  const [anchorEl, setAnchorEl] = useState(null);
  const [notificationsAllowed, setNotificationsAllowed] = useState(true);

  const handleMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const open = Boolean(anchorEl);

  return (
    <Box>
      {/* Icon to open the menu */}
      <IconButton onClick={handleMenuOpen}>
        <MoreVertIcon />
      </IconButton>

      {/* The Popover for user menu */}
      <Popover
        open={open}
        anchorEl={anchorEl}
        onClose={handleMenuClose}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'left' }}
        transformOrigin={{ vertical: 'top', horizontal: 'right' }}
      >
        <Box sx={{ p: 2, minWidth: 220 }}>
          {/* User Info */}
          <Box display="flex" alignItems="center" mb={2}>
            <Avatar
              src="https://via.placeholder.com/50"
              alt="User Avatar"
              sx={{ width: 48, height: 48, mr: 1 }}
            />
            <Box>
              <Typography variant="subtitle1" fontWeight="bold">
                Your Name
              </Typography>
              <Typography variant="body2" color="text.secondary">
                yourname@email.com
              </Typography>
            </Box>
          </Box>

          <List sx={{ p: 0 }}>
            <ListItem disablePadding>
              <ListItemButton onClick={() => alert('Go to My Profile')}>
                <ListItemText primary="My Profile" />
              </ListItemButton>
            </ListItem>
            <ListItem disablePadding>
              <ListItemButton onClick={() => alert('Go to Settings')}>
                <ListItemText primary="Settings" />
              </ListItemButton>
            </ListItem>
            <ListItem disablePadding>
              <ListItemButton onClick={() => alert('Notifications')}>
                <ListItemText primary="Notifications" />
              </ListItemButton>
            </ListItem>
            <ListItem disablePadding>
              <ListItemButton onClick={() => alert('Log Out')}>
                <ListItemText primary="Log Out" />
              </ListItemButton>
            </ListItem>
          </List>

          <Divider sx={{ my: 1 }} />

          {/* Toggle for "Allow" / "Mute" */}
          <Box display="flex" alignItems="center" justifyContent="space-between">
            <Typography variant="body2">Notifications</Typography>
            <Switch
              checked={notificationsAllowed}
              onChange={() => setNotificationsAllowed(!notificationsAllowed)}
            />
          </Box>
        </Box>
      </Popover>
    </Box>
  );
};

export default UserMenu;
